import React, { useEffect, useRef, useState } from 'react';

const videoIntro = "https://www.w3schools.com/tags/movie.mp4";
const videoLoop =
  "https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4";
const ScenePlayer = () => {
    const [vidIndex, setVidIndex] = useState(0);
    const ref = useRef(null);

    const canvasRef = useRef(null);
    const videoRef = useRef(null);
    const [currentScene, setCurrentScene] = useState(0);
    const [timeElapsed, setTimeElapsed] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    const scenes = [
        {
            index: 0,
            sentence: "This is a simple Javascript test",
            textPosition: { x: 640, y: 360 }, // Center
            textAnimation: "none",
            media: "https://miro.medium.com/max/1024/1*OK8xc3Ic6EGYg2k6BeGabg.jpeg", // Image URL
            duration: 5, // Scene 0 duration
        },
        {
            index: 1,
            sentence: "Here comes the video!",
            textPosition: { x: 640, y: 360 }, // Center
            textAnimation: "blink",
            media: "/VID.mp4", // Ensure your video is in the public folder
            duration: 3, // Scene 1 duration
        },
    ];


    useEffect(() => {
        if (vidIndex === 0 && ref.current) {
        ref.current.play();
        }
    }, [ref, vidIndex]);
    return (
        <div className="main">
        <video
            style={{ display: vidIndex === 1 ? "none" : "block" }}
            //src={videoIntro}
            src={scenes[0].media}
            autoPlay
            muted
            onEnded={() => setVidIndex((idx) => idx + 1)}
        />
        <video
            style={{ display: vidIndex === 0 ? "none" : "block" }}
            //src={videoLoop}
            src={scenes[1].media}
            muted
            loop
            ref={ref}
        />
        </div>
    );

}

export default ScenePlayer;
