import React, { useRef, useEffect } from 'react';

const ScenePlayer = () => {
    const videoRef = useRef(null);

    const scenes = [
        {
            index: 1,
            sentence: "Here comes the video!",
            textPosition: { x: 960, y: 100 },
            textAnimation: "blink",
            media: "/VID.mp4", // 确保你的文件在 public 文件夹
            duration: 5,
        },
    ];

    useEffect(() => {
        if (videoRef.current) {
            videoRef.current.play();
        }
    }, []);

    return (
        <div>
            <video
                ref={videoRef}
                src={scenes[0].media}
                style={{ display: 'block' }}
                onEnded={() => console.log("Video ended")}
                muted // 确保视频静音
                controls // 可选，允许用户手动控制
            />
        </div>
    );
};

export default ScenePlayer;
