import React, { useRef, useEffect, useState } from "react";

const ScenePlayer = () => {
  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const [currentScene, setCurrentScene] = useState(0);
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);

  const scenes = [
    {
      index: 0,
      sentence: "This is a simple Javascript test",
      textPosition: { x: 640, y: 360 }, // Center
      textAnimation: "none",
      media: "https://miro.medium.com/max/1024/1*OK8xc3Ic6EGYg2k6BeGabg.jpeg", // Image URL
      duration: 5, // Scene 0 duration
    },
    {
      index: 1,
      sentence: "Here comes the video!",
      textPosition: { x: 640, y: 360 }, // Center
      textAnimation: "blink",
      media: "/VID.mp4", // Ensure your video is in the public folder
      duration: 3, // Scene 1 duration
    },
  ];

  const drawScene = (ctx, scene) => {
    if (scene.index === 0) {
      // Draw image and text for scene 0
      const image = new Image();
      image.src = scene.media;
      image.onload = () => {
        ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
        ctx.drawImage(image, 0, 0, ctx.canvas.width, ctx.canvas.height);
        ctx.font = "30px Arial";
        ctx.fillStyle = "black";
        ctx.textAlign = "center";
        ctx.fillText(scene.sentence, scene.textPosition.x, scene.textPosition.y);
      };
    } else if (scene.index === 1) {
      // Play video for scene 1
      const video = videoRef.current;
      if (video) {
        video.src = scene.media;
        video.play();
        video.onplay = () => {
          const renderVideoFrame = () => {
            if (!video.paused && !video.ended) {
              ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
              ctx.drawImage(video, 0, 0, ctx.canvas.width, ctx.canvas.height);
              ctx.font = "30px Arial";
              ctx.fillStyle = "black";
              ctx.textAlign = "center";
              ctx.fillText(scene.sentence, scene.textPosition.x, scene.textPosition.y);
              requestAnimationFrame(renderVideoFrame);
            }
          };
          renderVideoFrame();
        };
      }
    }
  };

  useEffect(() => {
    const ctx = canvasRef.current.getContext("2d");
    const scene = scenes[currentScene];

    if (scene.index === 1) {
      setIsVideoPlaying(true);
    } else {
      setIsVideoPlaying(false);
    }

    drawScene(ctx, scene);

    const timer = setTimeout(() => {
      setCurrentScene((prev) => (prev + 1) % scenes.length);
    }, scene.duration * 1000);

    return () => {
      clearTimeout(timer);
      if (scene.index === 1) {
        const video = videoRef.current;
        if (video) {
          video.pause();
          video.currentTime = 0;
        }
      }
    };
  }, [currentScene]);

  return (
    <div>
      <canvas ref={canvasRef} width={1280} height={720} style={{ border: "1px solid black" }} />
      <video ref={videoRef} style={{ display: "none" }} />
    </div>
  );
};

export default ScenePlayer;
