import React, { useEffect, useRef, useState } from 'react';

//const videoIntro = "https://www.w3schools.com/tags/movie.mp4";
const videoIntro = "/VID.mp4";
const videoLoop =
"https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4";

const ScenePlayer = () => {
    const [vidIndex, setVidIndex] = useState(0);
    const introRef = useRef(null); // 指向第一个 video 元素
    const loopRef = useRef(null); // 指向第二个 video 元素

    useEffect(() => {
    if (vidIndex === 0 && introRef.current) {
    introRef.current.play();
    }
    }, [introRef, vidIndex]);

    return (
    <div className="main">
    <video
    style={{ display: vidIndex === 1 ? "none" : "block" }}
    src={videoIntro}
    autoPlay
    muted
    onEnded={() => setVidIndex((idx) => idx + 1)}
    ref={introRef} // 将 introRef 赋值给第一个 video 元素
    />
    <video
    style={{ display: vidIndex === 0 ? "none" : "block" }}
    src={videoLoop}
    muted
    loop
    ref={loopRef} // 将 loopRef 赋值给第二个 video 元素
    />
    </div>
    );
} 


export default ScenePlayer;
