import React, { useEffect, useRef, useState } from 'react';

const ScenePlayer = () => {
    const canvasRef = useRef(null);
    const videoRef = useRef(null);
    const [currentScene, setCurrentScene] = useState(0);
    const [timeElapsed, setTimeElapsed] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isTextVisible, setIsTextVisible] = useState(true);

    const scenes = [
        {
            index: 0,
            sentence: "This is a simple Javascript test",
            textPosition: { x: 640, y: 360 }, // Center
            textAnimation: "typing",
            media: "https://miro.medium.com/max/1024/1*OK8xc3Ic6EGYg2k6BeGabg.jpeg",//Yellow background：ECMAScript 6 A bright new future is coming.
            duration: 3,
        },
        {
            index: 1,
            sentence: "Here comes the video!",
            textPosition: { x: 960, y: 100 }, // Top-right
            textAnimation: "blink",
            //media: "https://media.gettyimages.com/videos/goodlooking-young-woman-in-casual-clothing-is-painting-in-workroom-video-id1069900546",
            media: "https://www.w3schools.com/html/mov_bbb.mp4",
            duration: 5,
        },
    ];

    useEffect(() => {
        const ctx = canvasRef.current.getContext('2d');

        const interval = setInterval(() => {
            setTimeElapsed(prev => prev + 0.1);
            if (timeElapsed >= scenes[currentScene].duration) {
                setCurrentScene(prev => (prev + 1) % scenes.length);
                setTimeElapsed(0);
            }
            drawScene(ctx);
        }, 100);

        return () => clearInterval(interval);
    }, [timeElapsed, currentScene]);

    useEffect(() => {
        let blinkInterval;
        if (scenes[currentScene].textAnimation === "blink") {
            blinkInterval = setInterval(() => {
                setIsTextVisible(prev => !prev);
            }, 500);
        } else {
            setIsTextVisible(true); // Reset visibility for non-blink scenes
        }

        return () => clearInterval(blinkInterval);
    }, [currentScene]);

    const drawScene = (ctx) => {
        const scene = scenes[currentScene];
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

        // Load and draw media
        if (scene.media.endsWith('.jpeg') || scene.media.endsWith('.jpg')) {
            const img = new Image();
            img.src = scene.media;
            img.onload = () => ctx.drawImage(img, 0, 0, canvasRef.current.width, canvasRef.current.height);
        } else {
            ctx.fillStyle = 'lightgray';
            ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        }

        drawText(ctx, scene.sentence, scene.textPosition.x, scene.textPosition.y);
    };

    const drawText = (ctx, text, x, y) => {
        if (scenes[currentScene].textAnimation === "blink" && !isTextVisible) {
            return; // 如果文本不可见则不绘制
        }

        ctx.fillStyle = 'black';
        ctx.font = '30px Arial';
        ctx.textAlign = 'center';
        ctx.fillText(text, x, y);
    };

    const handleCanvasClick = () => {
        if (currentScene === 1) { // 只在第1场景中处理视频
            if (isPlaying) {
                setIsPlaying(false);
                videoRef.current.pause();
            } else {
                setIsPlaying(true);
                videoRef.current.currentTime = 0; // 从头开始播放
                videoRef.current.play().catch(error => {
                    console.error('Error playing video:', error);
                });
            }
        }
    };

    useEffect(() => {
        // 当切换到其他场景时，停止视频并重置状态
        if (currentScene !== 1) {
            setIsPlaying(false);
            videoRef.current.pause();
            videoRef.current.currentTime = 0; // 重置视频时间
        }
    }, [currentScene]);

    return (
        <div>
            <canvas
                ref={canvasRef}
                width={1280}
                height={720}
                onClick={handleCanvasClick}
                style={{ cursor: 'pointer', backgroundColor: '#f0f0f0' }}
            />
            <video
                ref={videoRef}
                src="https://media.gettyimages.com/videos/goodlooking-young-woman-in-casual-clothing-is-painting-in-workroom-video-id1069900546"
                style={{ display: 'none' }} // Hide the video element
                onEnded={() => setIsPlaying(false)}
                muted
            />
        </div>
    );
};

export default ScenePlayer;
