import React, { useEffect, useRef, useState } from 'react';

const ScenePlayer = () => {
    const canvasRef = useRef(null);
    const videoRef = useRef(null);
    const [currentScene, setCurrentScene] = useState(0);
    const [timeElapsed, setTimeElapsed] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    const scenes = [
        {
            index: 0,
            sentence: "This is a simple Javascript test",
            textPosition: { x: 640, y: 360 }, // Center
            textAnimation: "none",
            media: "https://miro.medium.com/max/1024/1*OK8xc3Ic6EGYg2k6BeGabg.jpeg", // Image URL
            duration: 5, // Scene 0 duration
        },
        {
            index: 1,
            sentence: "Here comes the video!",
            textPosition: { x: 640, y: 360 }, // Center
            textAnimation: "blink",
            media: "/VID.mp4", // Ensure your video is in the public folder
            duration: 3, // Scene 1 duration
        },
    ];

    useEffect(() => {
        const ctx = canvasRef.current.getContext('2d');
        const interval = setInterval(() => {
            setTimeElapsed(prevTime => prevTime + 0.1);
            if (timeElapsed >= scenes[currentScene].duration) {
                setCurrentScene(prevScene => (prevScene === 0 ? 1 : 0)); // Toggle between scenes
                setTimeElapsed(0);
                //if (videoRef.current) {
                //    videoRef.current.pause();
                //    videoRef.current.currentTime = 0; // Reset video time
                //    setIsPlaying(false);
                //}
                drawScene(ctx); // 只在场景切换时调用 drawScene，而非每隔 100 毫秒调用一次
            }

        }, 100);
        return () => clearInterval(interval);
    }, [timeElapsed, currentScene]);

    const drawScene = (ctx) => {
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        const scene = scenes[currentScene];

        // Handle image for scene 0
        if (scene.index === 0) {
            const img = new Image();
            img.src = scene.media;
            if (img.complete) {
                ctx.drawImage(img, 0, 0, canvasRef.current.width, canvasRef.current.height);
                drawText(ctx, scene.sentence, scene.textPosition.x, scene.textPosition.y);
            } else {
                img.onload = () => {
                    ctx.drawImage(img, 0, 0, canvasRef.current.width, canvasRef.current.height);
                    drawText(ctx, scene.sentence, scene.textPosition.x, scene.textPosition.y);
                };
            }
        } else if (scene.index === 1) { // Handle video for scene 1
            ctx.fillStyle = 'lightgray';
            ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

            // 确保视频准备好了再绘制
            if (videoRef.current && videoRef.current.readyState >= 2) {
                ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
            } else {
                videoRef.current.oncanplay = () => {
                    ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
                };
            }

            // 绘制文本
            drawText(ctx, scene.sentence, scene.textPosition.x, scene.textPosition.y);
        }
    };

    const drawText = (ctx, text, x, y) => {
        if (currentScene === 0) {
            ctx.fillStyle = 'black';
            ctx.font = '30px Arial';
            ctx.textAlign = 'center';
            ctx.fillText(text, x, y);
        }
    };

    const handleCanvasClick = () => {
        if (currentScene === 1) {
            if (isPlaying) {
                setIsPlaying(false);
                videoRef.current.pause();
            } else {
                setIsPlaying(true);
                videoRef.current.play().catch(error => console.error('Error playing video:', error));

            }
             // 添加 blink 效果的 sentence
             const blinkText = document.createElement('span');
             blinkText.textContent = scenes[1].sentence;
             blinkText.style.position = 'absolute';
             blinkText.style.top = scenes[1].textPosition.y + 'px';
             blinkText.style.left = scenes[1].textPosition.x + 'px';
             blinkText.style.fontSize = '30px';
             blinkText.style.fontFamily = 'Arial';
             blinkText.style.textAlign = 'center';
             blinkText.style.animation = 'blink 1s infinite';
             document.body.appendChild(blinkText);
        }
    };

    const handleCanvasDoubleClick = () => {
        if (currentScene === 1) {
            setIsPlaying(false);
            videoRef.current.currentTime = 0;
            videoRef.current.pause();
        }
    };

    return (
        <div>
            <video
                src={scenes[currentScene].media}
                style={{ display: 'none' }} // Hide video element, we draw it on canvas
                autoPlay
                onCanPlay={() => {
                    console.log('Video is ready to play');
                }}
                onEnded={() => {
                    setIsPlaying(false);
                    setCurrentScene(0); // Switch back to scene 0 after video ends
                }}
                muted
                controls // Optional
                ref={videoRef}
            />
            <canvas
                ref={canvasRef}
                width={1280}
                height={720}
                onClick={handleCanvasClick}
                onDoubleClick={handleCanvasDoubleClick}
                style={{ cursor: 'pointer', backgroundColor: '#f0f0f0' }}
            />
            {currentScene === 1 && (
                <div>
                    <span
                        style={{
                            position: 'absolute',
                            top: scenes[1].textPosition.y,
                            left: scenes[1].textPosition.x,
                            fontSize: '30px',
                            fontFamily: 'Arial',
                            textAlign: 'center',
                            animation: 'blink 1s infinite',
                        }}
                    >
                        {scenes[1].sentence}
                    </span>
                </div>
            )}
        </div>
    );
};

export default ScenePlayer;
